   Product(id:string):Observable<IProduct>{
      return of(this.productsArr.find(u=>u.id===id)!)
    }
  
    AddProduct(obj:IProduct):Observable<IProduct>{
      this.productsArr.unshift(obj)
      return of(obj)
    }
  
    UpdateProduct(obj:IProduct):Observable<IProduct>{
      let getindex= this.productsArr.findIndex(u=>u.id===obj.id)
      this.productsArr[getindex] = obj
      return of(obj)
    }
  
    RemoveProduct(id:string){
      let getindex= this.productsArr.findIndex(u=>u.id===id)
      this.productsArr.splice(getindex,1)
      return of(id)
    }

